async function seedQuestions() {
  try {
    await AptitudeQuestion.deleteMany();

    const rawQuestions = [
  questionsHtml,
  questionsCss,
  questionsJS,
  questionsReact,
  questionsAngular,
  questionsVue,
  questionsCSharp,
  questionsGo,
  questionsNodeJs,
  questionsJava,
  questionsPython,
  questionsDotnet,
  questionsSpring,
  questionsDjango,
  questionsCPlusPlus,
  questionsFlutter,
  questionsTypescript,
  questionsSql,
  questionsNosql,
  questionsRust,
  questionsKotlin,
  questionsSwift,
];

//  Flatten safely
const allQuestions = rawQuestions.flatMap((set, setIndex) => {
  if (!Array.isArray(set)) {
    console.log(` Dataset at index ${setIndex} is not array`, set);
    return [];
  }

  return set.map((q, i) => {
    if (!q || !q.question) {
      console.log(` Invalid question at set ${setIndex} index ${i}`, q);
      return null;
    }

    return {
      question: q.question,
      options: q.options,
      answer: q.answer,
      marks: q.marks || 1,
      techStack: (q.techStack || "general").toLowerCase().replace(/\s+/g, "")
    };
  }).filter(Boolean);
});



    await AptitudeQuestion.insertMany(allQuestions);

    console.log(" MCQs Inserted Successfully!");
    process.exit();
  } catch (err) {
    console.error(err);
    process.exit(1);
  }
}


seedQuestions();
